/**
 * Fixify is a irregular elements padding and height value equalizer.
 * @author : Mucahit Tutuncu at Epigra
 */
var Fixify={attributes:{elements:"",heights:[],largeHeight:0,padding:0},measureMaxHeight:function(t){this.elements=document.querySelectorAll(t);for(var e=0,i=this.elements.length;i>e;e++)this.attributes.heights[e]=this.elements[e].clientHeight;this.attributes.largeHeight=Math.max.apply(Math,this.attributes.heights)},matchPadding:function(t){this.measureMaxHeight(t);for(var e=0,i=this.elements.length;i>e;e++)this.elements[e].clientHeight!=this.attributes.largeHeight&&(this.attributes.padding=(this.attributes.largeHeight-this.elements[e].clientHeight)/2,this.elements[e].style.padding=this.attributes.padding+"px 0px")},matchHeight:function(t){this.measureMaxHeight(t);for(var e=0,i=this.elements.length;i>e;e++)this.elements[e].clientHeight!=this.attributes.largeHeight&&(this.elements[e].style.height=this.attributes.largeHeight+2+"px")}};